start_point = None

puzzle_map = list()
for row, line in enumerate(puzzle_input.splitlines()):
  puzzle_map.append(line)
  if start_point is None:
    for col, value in enumerate(line):
      if value == 'S':
        start_point = [row,col]
        break
row_count = len(puzzle_map)
col_count = len(puzzle_map[0])

# Let's search inside the diamond
count = 0
# Upper part
for r in range(start_point[0]-64,start_point[0]):
  for c in range(start_point[1] - (r-1), start_point[1] + (r-1) + 1):
    if( (r+c)%2 == 0 and puzzle_map[r][c] == '.'):
      count += 1
# Lower part
for r in range(start_point[0],start_point[0]+65):
  for c in range(start_point[1] - (129-r), start_point[1] + (130-r)):
    if( (r+c)%2 == 0 and puzzle_map[r][c] == '.'):
      count += 1

print(f"Part One: {count}")

# Observing the puzzle input, it is composed by 4 triangles at the borders
# and one diamond at the center. I'm pretty sure that we can use this geometry
# to infer a counting algorithm to be used on Part Two